function prepare_event_listener(){document.getElementById("matrix").addEventListener("keyup",refresh)}function refresh(){refresh_list("matrix","sorted_list")}function refresh_list(a,b){clear_list(b),table_to_sorted_list(a,b)}function clear_list(a){document.getElementById(a).innerHTML=""}function table_to_sorted_list(a,b){var c=parse_html_table_into_cells(a);sort_cells_by_value_desc(c),cells_to_html_list(c,b)}function parse_html_table_into_cells(a){for(var e,b=[],c=document.getElementById(a),d=1;e=c.rows[d];d++)for(var g,f=1;g=e.cells[f];f++){var h=parse_cell_input_content(g.children[0].value);h.row_index=d,h.col_index=f,h.table=c,process_cell_data(h,b)}return b}function parse_cell_input_content(a){var b=clean_string(a),c={table:void 0,row_index:void 0,col_index:void 0,value:b,get_cell_in_html_table:function(){return this.table.rows[this.row_index].cells[this.col_index]}};return c}function clean_string(a){return a=a.replace(/[&]nbsp[;]/gi,""),a=a.replace(/[<]br[^>]*[>]/gi,""),a.trim()}function process_cell_data(a,b){a.value=parseInt(a.value),isNaN(a.value)||b.push(a)}function sort_cells_by_value_desc(a){a.sort(cell_sorter)}function cell_sorter(a,b){var c=b.value-a.value;return 0!==c?c:(c=a.col_index-b.col_index,0!==c?c:a.row_index-b.row_index)}function cells_to_html_list(a,b){var c="";a.forEach(function(a){c+=cell_to_html_list_element(a)}),document.getElementById(b).innerHTML=c}function cell_to_html_list_element(a){return'<li><div class="value">'+a.value+'</div>: <div class="coordinates">'+col_index_to_character(a.col_index)+a.row_index+"</div></li>\n"}function col_index_to_character(a){return String.fromCharCode(65+a-1)}prepare_event_listener();
